import { createContext, useContext, useState, useRef } from "react";

const ActiveSearchContext = createContext({
    activeSearch: false,
    updateActiveSearch: (value: boolean) => {},
    searchQuery: "",
    updateSearchQuery: (query: string) => {},
    scrollToTop: () => {},
    MainViewRef: null as React.MutableRefObject<HTMLDivElement> | null,
    scrollToProductView: () => {},
    ProductViewRef: null as React.MutableRefObject<HTMLDivElement> | null,
  });

export const useActiveSearchContext = () => {
    return useContext(ActiveSearchContext)
}

export const ActiveSearchProvider = ({ children }) => {
  const [activeSearch, setActiveSearch] = useState(false);
  const [searchQuery, setSearchQuery] = useState("")
  const MainViewRef = useRef<HTMLDivElement | null>()
  const ProductViewRef = useRef<HTMLDivElement | null>()

  const updateActiveSearch = (value: boolean) => {
    setActiveSearch(value);
  };

  const updateSearchQuery = (query:string) => {
    setSearchQuery(query);
  }

  const scrollToProductView = () => {
    if (ProductViewRef.current) {
      ProductViewRef.current.scrollIntoView({ behavior: "smooth" });
    }
  }

  const scrollToTop = () => {
    if (MainViewRef.current) {
      MainViewRef.current.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <ActiveSearchContext.Provider value={{ activeSearch, updateActiveSearch, searchQuery, updateSearchQuery, scrollToTop, MainViewRef, scrollToProductView, Pro }}>
      {children}
    </ActiveSearchContext.Provider>
  );
}

