import './ProductCard.css';
import {Furniture} from '../../types/types';
// import Button from '../ReusableComponents/Button/Button';

import { useState, use } from 'react';
import ProductPreviewModal from '../ProductPreviewModal/ProductPreviewModal';
interface ProductCardProps {
    productList: Furniture;
}

const ProductCard = ({productList}: ProductCardProps) => {

    const [isModalOpen, setIsModalOpen] = useState(false);
    const openModal = () => {
        setIsModalOpen(true);
    };
    const closeModal = () => {
        setIsModalOpen(false);
    }

    useEffect(() => {
        if (!isModalOpen) {
          setProductList(productList);
        }
      }, [isModalOpen, productList]);
    
    return (
    
        <div className='ProductCardWrapper'>
            <div className="ProductImageContainer">
                <img
                    src={productList.image}
                    alt="product img" />
            </div>
            <div className="ProductDataContainer">
                <div className="ProductName">
                    {productList.title}
                </div>
                <div className="ProductPrice">
                    {productList.price} EUR
                </div>
            </div>
            {/* <Button text='View' /> */}
            <button 
                className='ProductCardQuickviewButton'
                onClick={openModal}
                >
                Quickview
            </button>
            {isModalOpen && (
                <ProductPreviewModal
                isModalOpen={isModalOpen} // Pass isModalOpen as a prop
                openModal={openModal} // Pass openModal as a prop
                closeModal={closeModal} // Pass closeModal as a prop
                    productList={productList}
                />
            )}
        </div>
    );
}


export default ProductCard
