    const filteredProducts = FurnitureData.filter((item) =>
      Object.values(item).some((value) =>
        String().toLowerCase().includes(searchQuery.toLowerCase())
      )
    );
    setProducts(filteredProducts);
  }, [searchQuery]);
  //------------------

