import { createContext, useContext, useState } from "react";

const ActiveSearchContext = createContext();

export function useActiveSearchContext() {
    const context = useContext(ActiveSearchContext);
    return context
}

export function ActiveSearchProvider({ children }) {
  const [activeSearch, setActiveSearch] = useState(false);

  return (
    <ActiveSearchContext.Provider value={{ activeSearch, setActiveSearch }}>
      {children}
    </ActiveSearchContext.Provider>
  );
}
